function normalizaCadena(cadena) {
    // Definimos los caracteres que queremos eliminar
    var caracteresEspeciales = "!¡@#$^&%*()+=-[]\/{}|:<>?¿,.";

    // Eliminamos todos los caracteres que definimos arriba
    for (var i = 0; i < caracteresEspeciales.length; i++) {
        cadena = cadena.replace(new RegExp("\\" + caracteresEspeciales[i], 'gi'), '');
    }

    // pasamos la cadena en minusculas
    cadena = cadena.toLowerCase();

    // Quitamos espacios.
    cadena = cadena.replace(/ /g, "");

    // Quitamos acentos y "ñ".
    cadena = cadena.replace(/á/gi, "a");
    cadena = cadena.replace(/é/gi, "e");
    cadena = cadena.replace(/í/gi, "i");
    cadena = cadena.replace(/ó/gi, "o");
    cadena = cadena.replace(/ú/gi, "u");
    cadena = cadena.replace(/ñ/gi, "n");

    return cadena;
}



